플러그인 호출 및 생성  [ install해서 전역으로 사 ]
import dayjs from './plugins/dayjs'
export default {
  install(vue) {
    //생성된 vue 노드에 $day로 propertie 등록
    vue.config.globalProperties.$day = dayjs
    //setup 함수에서 접근하기 위한 등록
    vue.provide('dayjs', dayjs)
  }
}

root 경로에 import [main.js]
import dayjs from './plugins/dayjs'

사용 [ *.vue 화면에서 사용]
templat : {{ $day(nowDt).format('YYYY.MM.DD') }}
및
const dayjs = inject('dayjs')
templat : {{ dayjs(nowDt).format('YYYY/MM/DD HH:mm:ss') }}
